<template>
  <div class="custom-checkbox">
    <input
      type="checkbox"
      :id="checkboxId"
      class="vh"
      @change="onChange"
      :checked="checked"
    />
    <label :for="checkboxId">{{ label }}</label>
  </div>
</template>

<script>
export default {
  props: {
    label: String,
    checkboxId: String,
    checked: Boolean,
  },

  methods: {
    onChange(event) {
      this.$emit('input', event.target.checked)
    },
  },
}
</script>

<style lang="scss" scoped>
