//   padding-right: $input-padding-x;
// }
.vh {
  position: absolute !important;
  clip: rect(1px, 1px, 1px, 1px);
  padding: 0 !important;
  border: 0 !important;
  height: 1px !important;
  width: 1px !important;
  overflow: hidden;
}

/*  Add a :before to each label and style this as the checkbox you want to have */

label {
  display: flex;
  align-items: center;
  cursor: pointer;
}

label:before {
  content: '';
  width: 15px;
  height: 15px;
  border: 1px solid rgba(75, 101, 132, 0.3);
  margin-right: 16px;
}

input[type='checkbox']:checked ~ label:before {
  background: $dark no-repeat center;
  background-size: 8px 8px;
  background-image: url(data:image/svg+xml;utf8;base64,);
}

/*  Show when label and checkbox have focus, to make this keyboard accessible > press spacebar to select the checkbox */

input[type='checkbox']:checked ~ label {
  color: $dark;
}
</style>
